package ejercicio4;

public class ej4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// array de String que contiene los nombres de los estudiantes.
		String[] estudiantes = {"Micaela", "Sarah", "Thiago", "Elena"};
	        

		    //  La sintaxis for (String estudiante : estudiantes) significa "para cada String llamado estudiante en estudiantes".
	        for (String estudiante : estudiantes) {
	            // Imprimir un saludo personalizado para cada estudiante
	            System.out.println("Hola, " + estudiante + "!");
	        }
	    }
	
	}


